package neetCode150.slidingwindow;

import java.util.Arrays;
import java.util.List;
import java.util.stream.Collectors;
import java.util.stream.IntStream;
import java.util.stream.Stream;

public class Permutation_In_String {
    public static boolean checkInclusion(String s1, String s2) {
        if (s1 == null || s2 == null || s1.length() > s2.length()) return false;
        int [] arr1 = new int[26]; // Alphabets
        int [] arr2 = new int[26];

        IntStream.range(0, s1.length()).forEach(i -> {
            arr1[s1.charAt(i) - 'a']++;
            arr2[s2.charAt(i) - 'a']++;
        });
        if (Arrays.equals(arr1, arr2)) return true;

        int frontIndex = 0;
        int endIndex = s1.length();
        while (endIndex < s2.length()) {
            arr2[s2.charAt(frontIndex++) - 'a']--;
            arr2[s2.charAt(endIndex++) - 'a']++;
            if (Arrays.equals(arr1, arr2)) return true;
        }
        return false;
    }

    // Time Out
    public static boolean checkInclusion2(String s1, String s2) {
        if (s1 == null || s2 == null || s1.length() > s2.length()) return false;
        int i = 0;
        while (i + s1.length() <= s2.length()){
            if (Arrays.stream(s2.substring(i++, i + s1.length() - 1).split("")).sorted().toList()
                    .equals(Arrays.stream(s1.split("")).sorted().toList())) return true;
        }
        return false;
    }

    public static void main(String[] args) {
        System.out.println(checkInclusion("", ""));
    }
}
